import { useEffect, useState } from "react";

function Posts({ stories, getStories }) {

    const [storyMemes, setStoryMemes] = useState([]);
    const [postIsLiked, setPostIsLiked] = useState(false);
    const [memeLinks, setMemeLinks] = useState([])

    useEffect(() => {
        const newStoryMemes = stories.map(() => memeLinks[Math.floor(Math.random() * memeLinks.length)].link);
        console.log(newStoryMemes)
        setStoryMemes(newStoryMemes);
    }, [stories]);

    useEffect(() => {
        fetch('http://localhost:3000/memes')
            .then(response => response.json())
            .then(data => setMemeLinks(data));
    }, []);

    function handleLike(story, e) {
        e.preventDefault();
        console.log('Like button clicked');
        fetch(`http://localhost:3000/custom_stories/${story.id}`, {
            method: 'PATCH',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                "likes": story.likes + 1
            })
        })
        .then(() => getStories());
        setPostIsLiked(true);
    }



    const mappedStories = stories.flatMap((story, index) => {

        function getRandomColor() {
            const letters = '89ABCDEF';
            let color = '#';
            for (let i = 0; i < 6; i++) {
              color += letters[Math.floor(Math.random() * 8)];
            }
            return color;
        }

        const backgroundColor = getRandomColor();

        const storyCard = (
            <div class='individual-card' style={{ backgroundColor }}>                
                <p key={story.id}>{story.story}</p>
                <p>ð‘—Š</p>
                <p>Likes: {story.likes}</p>
                {postIsLiked ? <p>Post Liked!</p> : <button className="like-button" onClick={(e) => handleLike(story, e)}>Like</button> }
            </div>
        );

        if (index % 4 === 3) {
            const memeCard = (
                <div class='card individual-card text-center'>
                <img src={storyMemes[index]} key={storyMemes[index]} alt="Meme" style={{width:'100%'}}/>
                </div>
            );
            return [storyCard, memeCard];
        } else {
            return storyCard;
        }
    })

    return (
        <div className="posts-container">
            {mappedStories}
        </div>
    );
}

export default Posts;
